function hesaplaFiyat(pizzaFiyati, hamur) {
  let ekstraFiyat = 0;

  if (hamur === "orta") {
    ekstraFiyat = 5;
  } else if (hamur === "kalın") {
    ekstraFiyat = 10;
  }

  return pizzaFiyati + ekstraFiyat;
}
