import React from "react";
import "./Success.css";

const Success = (props) => {
  const { formData } = props.location.state || {};
  return (
    <div className="success">
      <h1>Teknolojik Yemekler</h1>
      <div className="text">
        <p className="lezzet-text"> TEBRİKLER</p>
        <p>SİPARİŞİNİZ ALINDI!</p>
      </div>
      <div>
        <p>Seçilen Hamur: {formData.dough}</p>
        <p>Seçilen Boyut: {formData.size}</p>
        <p>Ek Malzemeler: {formData.extraIngredient.join(", ")}</p>
        <p>Sipariş Notu: {formData.orderNotes}</p>
        <p>Toplam Fiyat: {formData.totalPrice}₺</p>
      </div>
    </div>
  );
};

export default Success;
